package cz.master.extern.babyradio.ratioresolver;import android.app.Activity;import android.util.Log;public class CalculateRatio {	private final static int CANVAS_WIDTH = 1600;	private final static int CANVAS_HEIGHT = 2560;	public static int SCREEN_WIDTH;	public static int SCREEN_HEIGHT;	public static double WIDTH_RATIO;	public static double HEIGHT_RATIO;	public static void calculateRatio(Activity activity) {		SCREEN_WIDTH = Screen.getScreenWidth(activity);		SCREEN_HEIGHT = Screen.getScreenHeight(activity);		Log.d("RatioResolver", "SCREEN_WIDTH = " + SCREEN_WIDTH);		Log.d("RatioResolver", "SCREEN_HEIGHT = " + SCREEN_HEIGHT);		if (CANVAS_HEIGHT > CANVAS_WIDTH) {			WIDTH_RATIO = (double) SCREEN_HEIGHT / (double) CANVAS_HEIGHT;			HEIGHT_RATIO = (double) SCREEN_HEIGHT / (double) CANVAS_HEIGHT;		} else {			WIDTH_RATIO = (double) SCREEN_WIDTH / (double) CANVAS_WIDTH;			HEIGHT_RATIO = (double) SCREEN_WIDTH / (double) CANVAS_WIDTH;		}	}}